import logo from './logo.svg';
import './App.css';
import {useState} from 'react'

function App() {

  const [ text , setText ] = useState('')
  const [count , setCount] =useState(0)

  function changeText(e) {
    setText(e.target.value)
  }

  function handleClick(e){
    e.preventDefault()
    const arr = text.split(" ")
    setCount(arr.length)
  }

  return (
    <div>
        <link href="https://fonts.googleapis.com/css?family=Press+Start+2P&display=swap" rel="stylesheet"></link>
        <h1>Let's Check your Typing Speed</h1>
        <textarea 
          name='text'
          value={text}
          onChange ={changeText}
        />
        <h4>Time remaining to catch up : </h4>
        <button onClick={handleClick}>Let's Start</button>
        <h1>Total word count is {count}</h1>
    </div>
  );
}

export default App;
